import base64
from PyQt5.QtGui import QPixmap

icon_base64 = '''

'''

def get_icon_pixmap():
    return decode_base64_to_pixmap(icon_base64)

def decode_base64_to_pixmap(base64_str):
    img_data = base64.b64decode(base64_str)
    pixmap = QPixmap()
    pixmap.loadFromData(img_data)
    return pixmap